const initialState = {
  filterQuery: '',
  productList: []
};
const product = (prevState = initialState, action) => {
  switch (action.type) {
    case 'SET_FILTER_QUERY':
      return Object.assign({}, prevState, {
        filterQuery: action.query   
      });
    case 'SET_PRODUCT_LIST':
      return Object.assign({}, prevState, {
        productList: aciton.productList
      });
    default:
      return prevState
  }
}

export default product;
